var loadBlogIndex=function(){requestJson("blog_index",function(e){e.forEach(function(e){var i=new Date(e.published_at),t='<a href="/blog'+e.href+'" class="blog-item-link"><div class="item"><div class="title">'+e.title+'</div><div class="sub-title">'+e.desc+'</div><div class="time">'+i.toDateString()+"</div></div></a>";$(".blog-index-list-container").prepend($(t))})})},requestJson=function(e,i){$.ajax({url:"/assets/json/"+e+".json",method:"GET"}).done(function(e){i(e)})};